use wmd;DROP TABLE IF EXISTS motionsensors_master2tracklet;CREATE TABLE  motionsensors_master2tracklet (  `sensor_id` int(10) unsigned NOT NULL default '0',  `start_time` bigint(20) unsigned NOT NULL default '0',  `tracklet_id` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`sensor_id`,`start_time`),  KEY `byTracklet` (`tracklet_id`,`start_time`,`sensor_id`),  KEY `byTime` (`start_time`,`sensor_id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;DROP TABLE IF EXISTS motionsensors_tracklets2;CREATE TABLE  motionsensors_tracklets2 (  `id` int(10) unsigned NOT NULL auto_increment,  `start_id` int(10) unsigned NOT NULL default '0',  `end_id` int(10) unsigned NOT NULL default '0',  `start_time` bigint(20) unsigned NOT NULL default '0',  `end_time` bigint(20) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`),  KEY `Index_2` (`start_time`),  KEY `Index_3` (`start_id`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;DROP TABLE IF EXISTS motionsensors_tracklets2_joints;CREATE TABLE  motionsensors_tracklets2_joints (  `id` int(10) unsigned NOT NULL auto_increment,  `id_from` int(10) unsigned NOT NULL default '0',  `id_to` int(10) unsigned NOT NULL default '0',  PRIMARY KEY  (`id`),  KEY `Index_2` (`id_to`),  KEY `Index_3` (`id_from`)) ENGINE=MyISAM DEFAULT CHARSET=latin1;